package hu.kuru.customerimport org.springframework.data.jpa.repository.JpaRepositoryimport org.springframework.data.jpa.repository.Queryinterface CustomerRepo extends JpaRepository<Customer, Long> {
	Customer findByName(String name)	Customer findByCode(String code)	@Query("select case when (count(c) > 0) then true else false end from Customer c where c.name = ?1")	boolean isExistsByName(String name)	@Query("select case when (count(c) > 0) then true else false end from Customer c where c.code = ?1")	boolean isExistsByCode(String code)	@Query("select case when (count(c) > 1) then true else false end from Customer c where c.name = ?1")	boolean isExistsMoreByName(String name)	@Query("select case when (count(c) > 1) then true else false end from Customer c where c.code = ?1")	boolean isExistsMoreByCode(String code)}
